import React from 'react';
import FormInput from '../form-input/form-input.component.jsx';
import './sign-in.styles.scss';

class SignIn extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            email: '',
            password: ''
        }
    }

    handleSubmit = event => {
        event.preventDefault();
        this.setState({email: '', password: ''});

    }

    handleChange = event => {
        const {value, name} = event.target;
        this.setState({[name]: value});  // a name valtozoban megkapom hogy a state melyik valtozojaba 
                                        // kell betolteni a value erteket. ezt a meghivasnal maga az
                                        //input tolti fel azzal a name ertekkel amit ott definialtam
    }

    render() {
        return (
            <div className='sign-in'>
                <h2> I have already have an account</h2>
                <span>Sign in with your email and password</span>

                <form onSubmit={this.handleSubmit}>
                    <FormInput 
                        name="email" 
                        type="email" 
                        value={this.state.email} 
                        handleChange={this.handleChange}
                        required />
                    <label>Email</label>
                    <FormInput 
                        name="password" 
                        type="password" 
                        value={this.state.password} 
                        handleChange={this.handleChange}
                        required />
                    <label>Password</label>
                    <input type="submit" values="Submit form" />
                </form>
            </div>
        );

    }

}

export default SignIn;
